class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int st = 0,end = k;
        int ans = n;
        map<int,vector<int>> mp;
        vector<int> mp1;
        for(int i=0;i<n/2;i++){
            int temp = max(nums[i],max(nums[n-1-i],max(abs(k-nums[i]),abs(k-nums[n-i-1]))));
            mp[abs(nums[i]-nums[n-i-1])].push_back(temp);
            mp1.push_back(temp);
        }
        sort(mp1.begin(),mp1.end());
        int cnt = 0;
        for(auto it:mp){
            int k = 0;
            for(auto it1:it.second){
                if(it1>=it.first) k+=1;
                else k+=2;
            }
            int cnt = lower_bound(mp1.begin(),mp1.end(),it.first)-mp1.begin();
            ans = min(ans,n/2+cnt-k);
        }
        return ans;

    }
};
