class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> map;
        vector<int> v;
        for(int i=0; i<n/2; i++){
            int mx = max(nums[i], nums[n-1-i]);
            int mn = min(nums[i], nums[n-1-i]);
            int x = mx - mn;
            map[x]++;
            int mxdif = max(k - mn, mx);
            v.push_back(mxdif);
        }
        sort(v.begin(), v.end());
        int res = 1e9;
        for(auto& [dif, cnt] : map){
            int idx = lower_bound(v.begin(), v.end(), dif) - v.begin();
            int l = idx, r = n/2 - idx;
            int t = l * 2 + r - cnt;
            res = min(res, t);
            // cout << dif << ", " << cnt << ", idx:" << idx << ", t:" << t << endl;
        }
        return res;
    }
};
