class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        priority_queue<int> qq;
        vector<int> diff(k + 1, 0);
        for (int i = 0; i < n / 2; ++i) {
            int d = abs(nums[i] - nums[n - 1 - i]);
            ++diff[d];
            int r = max({nums[i], nums[n - 1 - i], k - nums[i], k - nums[n - 1 - i]});
            qq.push(r);
        }

        int min_v = INT32_MAX;
        for (int i = k; i >= 0; --i) {
            while (!qq.empty() && qq.top() >= i) {
                qq.pop();
            }
            int change = n / 2 + qq.size() - diff[i];
            min_v = min(min_v, change);
        }

        return min_v;
    }
};
