class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        unordered_map<int,int> mp;

        for(int i=0; i<n/2; i++){
            int d = abs(nums[i] - nums[n-i-1]);
            mp[d]++;
        }

        vector<pair<int,int>> v;

        for(auto it: mp){
            v.push_back({it.second,it.first});
        }

        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());

        int res = INT_MAX;

        for(auto it:v){
            int diff = it.second;
            int f = 0;
            int ans = 0;
            for(int i=0; i<n/2; i++){
                int d = abs(nums[i] - nums[n-i-1]);

                if(d != diff){
                    int dr = k- min(nums[i], nums[n-i-1]);
                    int dy = max(nums[i], nums[n-i-1]) - 0;

                    if(dr<diff && dy<diff){
                        ans+=2;
                    }
                    else ans+=1;

                    if(ans>res){
                        f=1;
                        break;
                    } 
                }
            }

            res = min(ans,res);

            if(f==1) break;
        }

        return res;
    }
};
