typedef pair<int,int> pd;
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n=nums.size();
        for(int i=0;i<n/2;i++) mp[abs(nums[i]-nums[n-i-1])]++;
        priority_queue<pd> pq;
        int sum=0,res=INT_MAX;
        for(auto it:mp) {
            sum+=it.second;
            pq.push({it.second,it.first}); //{count,diffrence}
        }
        // int ans=sum-freq;
        int i=10;
        while(!pq.empty() && i>0){
            auto cur=pq.top();
            pq.pop();
            int count=cur.first,ans=sum-count,diff=cur.second;
            for(int i=0;i<n/2;i++){
                if(nums[i]+diff > k && nums[i]-diff<0 && nums[n-i-1]-diff<0 && nums[n-i-1]+diff>k) ans++;
            }
            res=min(res,ans);
            i--;
        }
        return res;
    }
};
