class Solution {
public:
        map<int,vector<int>> m;
        vector<int> v;
        int solve(vector<int>& nums) {
             int ans = nums.size(); 
             for( auto [diff,values]:m){
            int ind = lower_bound(v.begin(), v.end(), diff) - v.begin();
            ind = ind * 2 + (nums.size() / 2 - ind);
            for ( auto vl : values) {
                if (vl < diff) ind -= 2;
                 else  ind -= 1;
            }
            ans = min(ans, ind);
             }
            return ans;
        }
     int minChanges(vector<int>& nums, int k) {


        for (int i = 0; i < nums.size() / 2; ++i) {
            int diff = abs(nums[i] - nums[nums.size() - i - 1]);
             int vl = max({nums[i], nums[nums.size() - i - 1], k - nums[i], k - nums[nums.size() - i - 1]});
             m[diff].push_back(vl);
            v.push_back(vl);
        }
        sort(v.begin(), v.end());


        return solve(nums);
    }
};
