class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         map<int, vector<int>> m;
        vector<int> v;
        int i = 0;

        while (i < nums.size() / 2) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);
            int X = max({a, b, k - a, k - b});
            m[diff].push_back(X);
            v.push_back(X);
            i++;
        }
        int ans = nums.size();
        sort(v.begin(), v.end());
        auto it = m.begin();
        while (it != m.end()) {
            int diff = it->first;
            int T = lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (auto& X : it->second) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
            ++it;
        }

        return ans;
    }
};
