class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>arr(k+5, 0);
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            int a=nums[i], b=nums[n-1-i];
            int x=abs(a-b);
            int exclude=min(min(a, k-a), min(b, k-b));
            int upperlimit=k-exclude;
            arr[0]++;
            arr[upperlimit+1]--;
            arr[upperlimit+1]+=2;
            arr[k+1]-=2;
            if(x<=upperlimit){
                arr[x]--;
                arr[x+1]++;
            }else{
                arr[x]-=2;
                arr[x+1]+=2;
            }
        }
        int ans=arr[0];
        for(int i=1;i<=k;i++){
            arr[i]+=arr[i-1];
            ans=min(ans,arr[i]);
        }
        return ans;
    }
};
