class Solution {
    public int minChanges(int[] nums, int k) {
         TreeMap<Integer, List<Integer>> m = new TreeMap<>();
        List<Integer> v = new ArrayList<>();

        for (int i = 0; i < nums.length / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.length - i - 1];
            int diff = Math.abs(a - b);
            int X = Math.max(Math.max(a, b), Math.max(k - a, k - b));

            m.computeIfAbsent(diff, key -> new ArrayList<>()).add(X);
            v.add(X);
        }

        int ans = nums.length;
        Collections.sort(v);

        for (Map.Entry<Integer, List<Integer>> entry : m.entrySet()) {
            int diff = entry.getKey();
            List<Integer> values = entry.getValue();

            int T = lowerBound(v, diff);
            T = T * 2 + (nums.length / 2 - T);

            for (int X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = Math.min(ans, T);
        }

        return ans;
    }

    private int lowerBound(List<Integer> list, int key) {
        int left = 0, right = list.size();
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (list.get(mid) < key) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }
        return left;
    }
}
