class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> diffToMaxValues;
        vector<int> maxValues;

        int n = nums.size();
        int halfSize = n / 2;

        // Populate the map and vector with initial values
        for (int i = 0; i < halfSize; ++i) {
            int num1 = nums[i];
            int num2 = nums[n - i - 1];
            int difference = abs(num1 - num2);
            int maxValue = max({num1, num2, k - num1, k - num2});

            diffToMaxValues[difference].push_back(maxValue);
            maxValues.push_back(maxValue);
        }

        int minChanges = n;
        sort(maxValues.begin(), maxValues.end());

        // Evaluate the number of changes for each difference
        for (const auto& entry : diffToMaxValues) {
            int currentDiff = entry.first;
            int changeCount = lower_bound(maxValues.begin(), maxValues.end(), currentDiff) - maxValues.begin();
            changeCount = changeCount * 2 + (halfSize - changeCount);

            for (const auto& value : entry.second) {
                if (value < currentDiff) {
                    changeCount -= 2;
                } else {
                    changeCount -= 1;
                }
            }

            minChanges = min(minChanges, changeCount);
        }

        return minChanges;
    }
