class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> temp;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             int maxi = max({a, b, k - a, k - b});

             m[diff].push_back(maxi);
            temp.push_back(maxi);
        }

        int ans = nums.size();  
        sort(temp.begin(), temp.end());

        for ( auto [diff, values] : m) {
            int lo = lower_bound(temp.begin(), temp.end(), diff) - temp.begin();
            lo = lo * 2 + (nums.size() / 2 - lo);

            for ( auto val : values) {
                if (val < diff) {
                    lo -= 2;
                } else {
                    lo -= 1;
                }
            }

            ans = min(ans, lo);
        }

        return ans;
    }
};
