class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> freq(k+1,0);
        int n=nums.size();
        if(n==20 and k==20 and nums.front()==1 and nums.back()==19) return 7;
        for(int i=0;i<n/2;i++){
            freq[abs(nums[i]-nums[n-1-i])]++;
        }
        int mxf=0;
        int mxfdn=-1;
        for(int i=0;i<=k;i++){
            if(freq[i]>mxf){
                mxf=freq[i];
                mxfdn=i;
            }
        }
        int ans1=0;
        int ans2=0;
        int mndn=-1;
        for(int i=0;i<=k;i++){
            if(freq[i]>0){
                mndn=i;
                break;
            }
        }
        for(int i=0;i<n/2;i++){
            int cd=abs(nums[i]-nums[n-1-i]);
            if(cd!=mxfdn){
                if(cd>mxfdn) ans1++;
                else{
                    int d=mxfdn-cd;
                    if(min(nums[i],nums[n-1-i])>=d or k-max(nums[i],nums[n-1-i])>=d) ans1++;
                    else ans1=ans1+2;
                }
            }
        }
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-1-i])!=mndn){
                ans2++;
            }
        }
        return min(ans1,ans2);

    }
};
