#define ll long long int
#define pb push_back

#include <ext/pb_ds/assoc_container.hpp>
// Including tree_order_statistics_node_update
#include <ext/pb_ds/tree_policy.hpp>
using namespace __gnu_pbds;
template<class T> using pbds=tree<pair<ll,ll>, null_type,less<pair<ll,ll>>, rb_tree_tag,tree_order_statistics_node_update>;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        pbds<pair<ll,ll>> pq;
        ll ans=INT_MAX;
        ll n=nums.size();
        map<ll,ll> diff;
        map<ll,vector<ll>> m1;
        for(int i=0;i<n/2;i++){
            // cout<<nums[i]<<" "<<nums[n-1-i]<<endl;
            diff[abs(nums[i]-nums[n-1-i])]++;
            ll get=nums[i];
            ll gg=nums[n-1-i];
            get=max(get,gg);
            ll f=k-nums[i];
            ll g=k-nums[n-1-i];
            get=max(get,f);
            get=max(get,g);
            m1[abs(nums[i]-nums[n-1-i])].pb(get);
        }
        ll cnt=0;
        for(auto it:diff){
            cout<<it.first<<" "<<it.second<<endl;
            ll get=n/2;
            get-=it.second;
            ll x=pq.order_of_key({it.first,-1});
            //1 wala
            // cout<<x<<endl;
            ll curr=2*x;
            get-=x;
            curr+=get;
            // cout<<
            cout<<curr<<endl;
            ans=min(ans,curr);

            for(int i=0;i<m1[it.first].size();i++){
                pq.insert({m1[it.first][i],cnt});
                cnt++;
            }

        }
        return ans;

    }
};
