class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int>m;
        vector<int>vec(n/2,0);
        int res=INT_MAX;

        for(int i=0;i<n/2;i++){
            int mini=min(nums[i],nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            vec[i]=(max(maxi,k-mini));
            m[abs(nums[i]-nums[n-i-1])]++;
        }
        int s=vec.size();
        sort(vec.begin(),vec.end());
        for(auto x:m){
            int i=0;
            int j=s-1;
            while(i<=j){
                int mid=(j+i)/2;
                if(vec[mid]>=x.first)
                    j=mid-1;
                else
                    i=mid+1;
            }
            int pos=(i*2+(s-i-x.second));
            res=min(res,pos);
        }
        return res;
    }
};
