func abs(a int) int {
    if a > 0 {
        return a
    }
    return -a
}

func minChanges(nums []int, k int) int {
    n := len(nums)
    ans := n
    dict := make(map[int][]int)
    v := make([]int, 0)
    var diff, X, lid int
    for l:=0; l<n/2; l++ {
        diff = abs(nums[l]-nums[n-l-1])
        X = max(nums[l], nums[n-l-1], k-nums[l], k-nums[n-l-1])
        dict[diff] = append(dict[diff], X)
        v = append(v, X)
    }

    sort.Ints(v)


    for key, val := range dict {
        lid = n
        for i, t := range v {
            if t>=key {
                lid = i
                break
            }
        }
        lid = lid*2 +(n/2-lid)

        for _, j := range val {
            if j < key {
                lid-=2
            } else {
                lid-=1
            }
        }
        ans = min(ans, lid)

    }


    return ans
}
