class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dic = [0] * (k + 1)
        cnt = [0] * (k + 1)
        for i in range(n // 2):
            dic[abs(nums[i] - nums[n - 1 - i])] += 1
            cnt[max(nums[i], nums[n - 1 - i], k - nums[i], k - nums[n - 1 - i])] += 1
        s = [0] * (k + 1)
        for i in range(1, k + 1):
            s[i] = s[i - 1] + cnt[i]
        ans = n // 2 - dic[0]
        for i in range(1, k + 1):
            ans = min(ans, n // 2 - dic[i] + s[i - 1])
        return ans
