class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int N = 2e5 + 10;
        std::vector<int> cnt(N);
        for (int i = 0; i < n /2 ; ++i) {
            cnt[abs(nums[i] - nums[n - i - 1])]++;
        }
        std::vector<pair<int, int>> range;
        for (int i = 0; i < n / 2; ++i) {
            std::vector<int> tmp = {0, abs(k - nums[i]), abs(k - nums[n - i - 1]), abs(nums[i]), abs(nums[n - i - 1])};
            sort(tmp.begin(), tmp.end());
            range.emplace_back(tmp[0], tmp.back());
        }
        for (int i = 0; i < n / 2; ++i) {
            printf("%d %d\n", range[i].first, range[i].second);
        }
        puts("");
        std::vector<int> no_need_extra(N);
        for (int i = 0; i < n / 2; ++i) {
            no_need_extra[range[i].first] += 1;
            no_need_extra[range[i].second + 1] -= 1;
        }
        for (int i = 1; i < N; ++i) {
            no_need_extra[i] += no_need_extra[i - 1];
        }
        int mx = -n;
        for (int i = 0; i < N; ++i) {
            mx = max(mx, cnt[i] -( n / 2 - no_need_extra[i]));
        }

        return n / 2 - mx;
    }
};
