class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>diff;
        int n = nums.size();
        unordered_map<int,int>ff;
        int mf = 0;
        for(int i=0;i<(n/2);i++){
            int d = abs(nums[i] - nums[n-1-i]);
            diff.push_back(abs(nums[i] - nums[n-1-i]));
            ff[d]++;
            mf = max(ff[d],mf);
        }

        vector<int>ds;
        for(auto it:ff){
            if(it.second == mf || (it.second == (mf-1)))
                ds.push_back(it.first);
        }


        int ans = INT_MAX;

        for(int i:ds){
            int curr = 0;
            for(int j = 0;j<diff.size();j++){
                if(diff[j] == i)
                    continue;

                else{
                    int li = j,ri = (n-1-j);
                    int b = max(nums[li],nums[ri]),s = min(nums[li],nums[ri]);

                    if((((b-i)>=0) && ((b-i)<=k)) ||  (((i+s)>=0) && ((i+s)<=k)))
                        curr+=1;

                    else
                        curr+=2;
                }
            }

            ans = min(ans,curr);
        }

        return ans;
    }
};
