class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        Map<Integer, Integer> map = new HashMap<>();

        for (int i = 0; i < n / 2; i++) {
            int gap = Math.abs(nums[i] - nums[n - i - 1]);
            map.put(gap, map.getOrDefault(gap, 0) + 1);
        }

        List<int[]> list = new ArrayList<>();
        map.forEach((key, value) -> {
            list.add(new int[] {key, value});
        });

        Collections.sort(list, (o1, o2) -> {
            if (o1[1] == o2[1]) {
                return o1[0] - o2[0];
            }
            return o2[1] - o1[1];
        });

        int res = Integer.MAX_VALUE;
        for (int j = 0; j < Math.min(2, list.size()); j++) {
            int bestGap = list.get(j)[0];
            int cnt = 0;
            for (int i = 0; i < n / 2; i++) {
                int a = Math.min(nums[i], nums[n - i - 1]);
                int b = Math.max(nums[i], nums[n - i - 1]);
                int gap = b - a;
                if (gap != bestGap) {
                    if (gap < bestGap) {
                        if (b < bestGap && k - a < bestGap) {
                            cnt += 2;
                        } else {
                            cnt++;
                        }
                    } else {
                        cnt++;
                    }
                }
            }
            res = Math.min(res, cnt);
        }


        return res;
    }
}
