class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> m;
        int max_same=INT_MIN;
        set<int> st;
        for(int i=0;i<n/2;i++)
        {
            m[abs(nums[i]-nums[n-i-1])]++;
            if(m[abs(nums[i]-nums[n-i-1])] > max_same)
            {
                max_same=m[abs(nums[i]-nums[n-i-1])];
                st.insert(abs(nums[i]-nums[n-i-1]));
            }
            // else if(m[abs(nums[i]-nums[n-i-1])] == max_same && abs(nums[i]-nums[n-i-1]) < diff)
            // {
            //     st.insert(abs(nums[i]-nums[n-i-1]));
            // }
        }
        for(auto it:m)
        {
            if(it.second + 1 == max_same || it.second + 2 == max_same || it.second == max_same)
                st.insert(it.first);
        }
        int final_ans=INT_MAX;
        for(auto it:st)
        {
            int ans=0,diff=it;
            for(int i=0;i<n/2;i++)
            {
                if(abs(nums[i]-nums[n-i-1]) != diff)
                {
                    int diff_orig = abs(nums[i]-nums[n-i-1]);
                    if(diff_orig > diff)
                        ans++;
                    else
                    {
                        int maxi = max(nums[i],nums[n-i-1]);
                        int mini = min(nums[i],nums[n-i-1]);
                        if((maxi + (diff - diff_orig) <= k) || (mini - (diff - diff_orig) >=0))
                            ans++;
                        else ans+=2;
                    }
                }
            }
            final_ans=min(final_ans,ans);
        }
        return final_ans;
    }
};
