class Solution {
    public int minChanges(int[] a, int k) {
        int cnt[] = new int[100001];
        int n = a.length;
        int b[] = new int[n];
        for(int i = 0; i < n / 2; i ++){
            int gap = Math.abs(a[i] - a[n - i - 1]);
            cnt[gap] ++;
            b[i] = gap;
        }
        int ans = (int)1e9;
        // System.out.print(Arrays.toString(b));
        // System.out.print(Arrays.toString());
        for(int i = 0; i < n / 2; i ++){
            int gap = b[i];
            if(n / 2 - cnt[gap] >= ans) continue;
            int c = 0;
            for(int j = 0; j < n / 2; j ++){
                if(b[j] != gap){
                    if(Math.max(a[j], a[n - j - 1]) >= gap || k - Math.min(a[j], a[n - j - 1]) >= gap) c ++;
                    else c += 2;
                }
                if(c >= ans) break;
            }
            ans = Math.min(ans, c);
        }
        return ans;
    }
}
