class Solution {
public:
    int moves(vector<int>a,int k,int x,int m_first){
        int n=a.size();
        for(int i=0;i<a.size()/2;i++){
            if((abs(k-a[i]))>=x || a[i]>=x || abs(k-a[n-i-1])>=x || a[n-i-1]>=x){continue;}
            else m_first--;
        }
        return m_first;
    }
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int x=abs(a[i]-a[n-i-1]);
            mp[x]++;
        }
        int m_first=0;
        int m_second=0;
        vector<int>d;
        for(auto it:mp){
            if(m_first<it.second){
                m_second=m_first;
                m_first=it.second;
            }
        }
        vector<int>f;
        for(auto it:mp){
            if(m_first==it.second ){
                d.push_back(it.first);
            }
            if(m_second==it.second){
                f.push_back(it.first);
            }
        }
        int maxi=0;
        for(int i=0;i<d.size();i++){
            maxi=max(maxi,moves(a,k,d[i],m_first));
        }  
         for(int i=0;i<f.size();i++){
            maxi=max(maxi,moves(a,k,f[i],m_second));
        }        
        return n/2-maxi;
    }
};
