class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mpp;
        vector<int> answer;
        int l = nums.size();
        for (int i = 0; i < l / 2; i++) {
            int first_1 = nums[i];
            int second_2 = nums[nums.size() - i - 1];
            int difference = abs(first_1 - second_2);

             int X = max({first_1, second_2, k - first_1, k - second_2});

             mpp[difference].push_back(X);
            answer.push_back(X);
        }

        int resultant = l;  
        sort(answer.begin(), answer.end());

        for (auto& [difference, value] : mpp) {
            int TT = lower_bound(answer.begin(), answer.end(), difference) - answer.begin();
            TT = TT * 2 + (l/ 2 - TT);

            for (auto& X : value) {
                if (X < difference) {
                    TT -= 2;
                } else {
                    TT -= 1;
                }
            }

            resultant = min(resultant, TT);
        }

        return resultant;
