class Solution {
public:
    int binSearch(const vector<int>& nums, int target) {
    int left=0, right=nums.size();

    while (left < right) {
        int mid=left+(right - left)/2;
        if (nums[mid] < target) {
            left=mid+1;
        } else {
            right=mid;
        }
    }

    return left;
}
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> mp;
        int ans=INT_MAX;
        int n=nums.size();
        vector<int> dif;
        for(int i=0;i<nums.size()/2;i++)
        {
            int diff=abs(nums[i]-nums[n-i-1]);
            int maxi=INT_MIN;
            maxi=max(maxi,nums[i]-0);
            maxi=max(maxi,nums[n-i-1]-0);
            maxi=max(maxi,k-nums[i]);
            maxi=max(maxi,k-nums[n-i-1]);
            dif.push_back(maxi);
            mp[diff].push_back(maxi);
        }
        // for(auto i:dif)cout<<i<<endl;
        sort(dif.begin(),dif.end());
        int count=0;
        for(auto i:mp)
        {
            count=binSearch(dif,i.first);
            count=count*2+(n/2-count);
            for(int j=0;j<i.second.size();j++)
            {
                if(i.second[j]>=i.first){
                    count--;
                }
                else{
                    count-=2;
                }
            }
            ans=min(ans,count);
            // cout<<ans<<endl;
        }
        return ans;
    }
};
