class Solution {
public:
    void solve(vector<int>& nums,int n,map<int,vector<int>>&mpp,vector<int>&arr,int k)
    {
        for(int i = 0;i<n/2;i++)
        {
            int x = nums[i];
            int y = nums[n-i-1];
            int rem = abs(x-y);
            int val = max({x,y,k-x,k-y});
            arr.push_back(val);
            mpp[rem].push_back(val);
        }
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>arr;
        map<int,vector<int>> mpp;
        solve(nums,n,mpp,arr,k);
        sort(arr.begin(),arr.end());
        int res = n;
        for(auto it:mpp)
        {
            int value = it.first;
            int idx = lower_bound(arr.begin(),arr.end(),value)-arr.begin();
            idx = (n/2-idx)+(idx*2);
            for(auto i:it.second)
            {
                if(i<value) idx-=2;
                else idx-=1;
            }
            res = min(res,idx);
        }
        return res;
    }
};
