class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> varvector;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int randx = max({a,b,k-a,k-b});
            m[diff].push_back(randx);
            varvector.push_back(randx);
        }
        int answervar = nums.size();
        sort(varvector.begin(),varvector.end());

        for(auto &val:m){

            int diff = val.first;
            int tempvect = lower_bound(varvector.begin(),varvector.end(),diff)-varvector.begin();
            tempvect = tempvect*2+(nums.size()/2-tempvect);

            for(auto &randx:val.second){
                if(randx<diff){
                    tempvect-=2;
                }
                else{
                    tempvect-=1;
                }
            }

            answervar = min(answervar,tempvect);

        }

        return answervar;

    }
};
