class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        lst = [];
        cnt = [0 for i in range(k+1)];
        n = len(nums);
        for i in range(n//2):
            maxi1 = max(nums[i],k-nums[i]);
            maxi2 = max(nums[n-1-i],k-nums[n-1-i]);
            lst.append(max(maxi1,maxi2));
            cnt[abs(nums[i]-nums[n-i-1])] = cnt[abs(nums[i]-nums[n-i-1])]+1;
        lst.sort();
        def bisch(x):
            l = 1;
            r = len(lst);
            while l<r:
                mid = (l+r+1)//2;
                if lst[mid-1]<x:
                    l = mid;
                else:
                    r = mid-1;
            if lst[l-1]>=x:
                return 0;
            return l;
        res = 100001;
        for i in range(k+1):
            t = bisch(i);
            cost = 2*t + n//2 - t -cnt[i];
            res = min(cost,res);
        return res;
