struct entry{
    int diff;
    int count;
};

int cmpfnc(struct entry ** a, struct entry ** b){

    return (*b)->count - (*a)->count;
}
int minChanges(int* nums, int numsSize, int k) {

    if(numsSize == 2) return 0;
    if(k==0) return 0;

    struct entry ** list = malloc(sizeof(struct entry *) * (k+1));
    for(int i=0 ; i<=k ; i++){
        list[i] = malloc(sizeof(struct entry));
        list[i]->diff = i;
        list[i]->count = 0;
    }

    for(int i=0 ; i<numsSize ; i++){
        int diff = abs(nums[i] - nums[numsSize -1 -i]);
        list[diff]->count++;
    }

    qsort(list, k+1, sizeof(struct entry*), cmpfnc);

    // printf("check diff %d and %d\n", list[0]->diff, list[1]->diff);
    int first = list[0]->diff;
    int second = list[1]->diff;
    int result1 = 0;
    int result2 = 0;

    for(int i=0 ; i<numsSize/2 ; i++){
        if(abs(nums[i] - nums[numsSize -1 -i]) == first) continue;
        else{
            if(abs(nums[i] - 0) < first && abs(nums[i] - k) < first && abs(nums[numsSize-1-i] - 0) < first && abs(nums[numsSize-1-i] -k ) < first){
                result1 += 2;
            }else{
                result1 += 1;
            }
        }
    }
    for(int i=0 ; i<numsSize/2 ; i++){
        if(abs(nums[i] - nums[numsSize -1 -i]) == second) continue;
        else{
            if(abs(nums[i] - 0) < second && abs(nums[i] - k) < second && abs(nums[numsSize-1-i] - 0) < second && abs(nums[numsSize-1-i] -k ) < second){
                result2 += 2;
            }else{
                result2 += 1;
            }
        }
    }

    // printf("%d %d\n" , result1, result2);
    return result1 < result2 ? result1: result2;
}
