class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans = nums.size();
        vector<int> temp;
        unordered_map<int, vector<int>> m;

        for (int i=0; i<nums.size()/2; i++) 
        {
            temp.push_back(max({nums[i], nums[nums.size() - i - 1], k - nums[i], k - nums[nums.size() - i - 1]}));
            m[abs(nums[i]-nums[nums.size()-i-1])].push_back(max({nums[i], nums[nums.size() - i - 1], k - nums[i], k - nums[nums.size() - i - 1]}));
        }
        sort(temp.begin(), temp.end());  

        for (auto i : m) {
            int ind = lower_bound(temp.begin(), temp.end(), i.first) - temp.begin();
            ind = ((nums.size()/2)) +  ind;
            for (auto j : i.second) 
            {
                if (j >= i.first) ind -= 1;
                else ind -= 2;
            }
            ans = min(ans, ind);
        }
        return ans;
    }
};
