public class Solution {
    public int MinChanges(int[] nums, int k) {
        int n=nums.Length;
        //修改nums 使得nums对称位置相减的绝对值都是同一个x
        //新建dictionary 存放对称位置相减绝对值 统计相同数字数量 最多的那个就是临时的x
        //判断x可行性
        var abs=new Dictionary<int,int>();
        for(int i=0;i<n/2;i++){
            if(abs.ContainsKey(Math.Abs(nums[i]-nums[n-i-1])))
                abs[Math.Abs(nums[i]-nums[n-i-1])]++;
            else
                abs[Math.Abs(nums[i]-nums[n-i-1])]=1;
        }
        var q=new PriorityQueue<int,int>();
        foreach(var i in abs){
            q.Enqueue(i.Key,-i.Value);
        }
        int MinRes=int.MaxValue;
        //从q中获得出现次数最多的数 作为x
        int testCount=0;
        while(q.Count>0&&testCount<10){
            int res=0;
            testCount++;
            int x=q.Dequeue();
            for(int i=0;i<n/2;i++){
                int min=Math.Min(nums[i],nums[n-i-1]);
                int max=Math.Max(nums[i],nums[n-i-1]);
                //现在有以下情况
                //1 max-min=x 什么都不做
                //2 max>x 只需要减小min就行 res++ 
                //3 max<x 至少需要扩大max res++ 若k-min<x 则还需要修改min res++
                if(max-min==x){
                }else if(max>x){
                    res++;
                }else if(max<x){
                    res++;
                    if(k-min<x)res++;
                }else{
                    res++;
                }
            }
            MinRes=Math.Min(MinRes,res);
        }  
        return MinRes;
    }
}
