class Solution {
    private:
        int returnCount(int diffWithMaxFreq,vector<int> &nums,int k){
            int n=nums.size();
            int count = 0;
            int i=0;
            int j=n-1;
            while(i<j){
                int a = nums[i];
                int b = nums[j];
                if(abs(a-b)!=diffWithMaxFreq){
                    int maxi = max(a,b);
                    int mini = min(a,b);
                    if(abs(a-b)>diffWithMaxFreq){
                        count+=1;
                    }
                    else{
                        int extra = diffWithMaxFreq - abs(a-b);
                        if(mini - extra < 0){
                            if(maxi + extra > k)count+=2;
                            else count+=1;
                        }
                        else count+=1;
                    }
                }
                i++;
                j--;
            }
            return count;
        }
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mp;
        int i=0;
        int j=n-1;
        int diffWithMaxFreq = INT_MAX;
        vector<int> diffArray;
        while(i<j){
            diffArray.push_back(abs(nums[i]-nums[j]));
            i++;
            j--;
        }
        sort(diffArray.begin(),diffArray.end());
        diffWithMaxFreq = diffArray[(diffArray.size()-1)/2];
        int count1 = returnCount(diffWithMaxFreq,nums,k);

        diffWithMaxFreq = INT_MAX;
        int maxi=0;
        i=0;
        j=n-1;
        while(i<j){
            mp[abs(nums[i]-nums[j])]++;
            if(maxi<mp[abs(nums[i]-nums[j])]){
                maxi=mp[abs(nums[i]-nums[j])];
                diffWithMaxFreq = abs(nums[i]-nums[j]);
            }
            else if(maxi<=mp[abs(nums[i]-nums[j])]){
                diffWithMaxFreq = min(diffWithMaxFreq,abs(nums[i]-nums[j]));
            }
            i++;
            j--;
        }
        int count2= returnCount(diffWithMaxFreq,nums,k);
        return min(count1,count2);

    }
};
