class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> a;
        map<int, vector<int>> b;
        int c = 0;
        while(c < nums.size() / 2) {
            int d = nums[c], e = nums[nums.size() - c - 1];
            int f = abs(d - e);
            int g = max({d, e, k - d, k - e});
            b[f].push_back(g);
            a.push_back(g);
            c++;
        }

        sort(a.begin(), a.end());
        int h = nums.size();
        for(auto &i : b) {
            int f = i.first;
            int j = lower_bound(a.begin(), a.end(), f) - a.begin();
            j = j * 2 + (nums.size() / 2 - j);

            for(auto &g : i.second) {
                j -= (g < f) ? 2 : 1;
            }

            h = min(h, j);
        }

        return h;
    }
};
