class Solution {
public:

    #define ll long long 

    ll bs(vector<ll>& v , ll d)
    {
        ll low=0;
        ll high=v.size()-1;
        ll ans=-1;

        while(low<=high)
        {
            ll mid=low+(high-low)/2;
            if (v[mid]>=d)
            {
                ans=mid;
                high=mid-1;
            }
            else
                low=mid+1;
        }

        return ans;
    }


    void findmax(ll n , vector<ll>& v , map<ll,vector<ll>>& mp ,vector<int>& nums , ll k)
    {
        for (int i=0;i<n/2;i++)
        {
            ll maxi=max({nums[i],nums[n-i-1]});
            maxi=max(maxi,(ll)k-nums[i]);
            maxi=max(maxi,(ll)k-nums[n-i-1]);
            ll d=abs(nums[i]-nums[n-i-1]);
            mp[d].push_back(maxi);
            v.push_back(maxi);
        }

    }
    int minChanges(vector<int>& nums, int k) {

        ll n=nums.size();
        vector<ll>v;
        map<ll,vector<ll>>mp;

        findmax(n,v,mp,nums,k);

        sort(v.begin(),v.end());

        ll ans=n;
        for (auto x:mp)
        {
            ll d=x.first;
            ll find=bs(v,d);

            find=find*2+( (n/2)-find);
            bool f=false;
            for (auto v:x.second)
            {
                f=true;
                if(v<d) find-=2;
                else
                    find-=1;
            }
            if (f)
            ans=min(ans,find);
        }


        return ans;




    }
};
