int cnt[100005];
class Solution {
public:
    int minChanges(vector<int>& b, int k) {
        int i,j;
        int n = b.size();
        for (i=0;i<=k;++i) cnt[i] = 0;
        for (i=0;i<n-i-1;++i) {
            //printf("i %d\n", i);
            cnt[0]++;
            int x = b[i];
            int y = b[n-i-1];
            cnt[abs(x-y)]--;
            cnt[abs(x-y) + 1]++;
            int xx = max(max(k-x, k-y), max(x, y));
            //printf("%d\n", abs(x-y));
            //printf("%d\n", abs(x-y) + 1);
            //printf("%d\n", xx);
            cnt[xx+1]++;
        }
        int ans = n;
        int now = 0;
        for (i=0;i<=k;++i) {
            //printf("%d\n", cnt[i]);
            now += cnt[i];
            ans = min(ans, now);
        }
        return ans;
    }
};
