const int N = 1e5 + 5;
int c[N];

void init() {
    memset(c, 0, sizeof c);
}

void add(int x) {
    for (; x <= 100000; x += -x & x) ++c[x];
}

int ask(int x) {
    int ans = 0;
    for (; x; x -= -x & x) ans += c[x];
    return ans;
}

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        init();
        unordered_map<int, int> st;
        for (int i = 0, j = nums.size() - 1; i < j; ++i, --j) {
            ++st[abs(nums[j] - nums[i])];
            int mx = max(max(nums[i], k - nums[i]), max(nums[j], k - nums[j]));
            add(mx + 1);
        }
        int ans = nums.size();
        for (auto &p : st) {
            ans = min(ans, ask(p.first) + (int(nums.size()) - p.second * 2) / 2);
            //cout << p.first << ' ' << p.second << '\n';
        }
        return ans;
    }
};
