class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n=len(nums)
        dc=[0]*(k+1)
        for i in range(n//2):
            diff=abs(nums[i]-nums[n-i-1])
            far=max(k-min(nums[i],nums[n-i-1]),max(nums[i],nums[n-i-1]))+1
            dc[0]+=1
            dc[diff]-=1
            if diff+1<=k:
                dc[diff+1]+=1
            if far<=k:
                dc[far]+=1
        for i in range(k):
            dc[i+1]+=dc[i]
        return min(dc)
