class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        difs = Counter(abs(nums[i] - nums[~i]) for i in range(len(nums) // 2))
        max_difs = [
            max(nums[i], nums[~i], k - nums[i], k - nums[~i])
            for i in range(len(nums) // 2)
        ]
        max_difs.sort()
        ans = float("inf")
        for i in range(k + 1):
            idx = bisect_left(max_difs, i)
            ans = min(ans, 2 * idx + len(max_difs) - idx - difs.get(i, 0))
        return ans
