class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>ct,p;
        for(int i=0;i<n/2;i++){
            int low=min(nums[i],nums[n-i-1]);
            int high=max(nums[i],nums[n-i-1]);
            p[max(high,k-low)]++;
            ct[high-low]++;
        }
        int ans=INT_MAX,two=0;
        for(int i=0;i<=k;i++){
            int temp;

            temp=(n/2)-ct[i];
            temp+=two;
            ans=min(ans,temp);
            two+=p[i];
        }
        return ans;
    }
};
