class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = 1e9;
        vector<int> v(k + 1);
        for (int i = 0; i < n>>1; ++i) v[abs(nums[i]-nums[n-i-1])]++;
        // for (auto x: v) cout << x << ' '; cout << endl;
        vector<int> vv(n + 1, -1);
        for (int i = 0; i <= k; ++i) {
            if (vv[v[i]] == -1) vv[v[i]] = i;
        }
        // for (auto x: vv) cout << x << ' ';
        for (int i = n; i>-1; --i) {
            if (vv[i] == -1) continue;
            int anss = 0;
            for (int j = 0; j < n>>1; ++j) {
                if (abs(nums[j]-nums[n-j-1]) == vv[i]) continue;
                int a = nums[j], b = nums[n-j-1];
                if (a>b) swap(a,b);
                if (k - a < vv[i] && b < vv[i]) anss += 2;
                else ++anss;
            }
            ans = min(ans, anss);
        }
        return ans;
    }
};
