class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> sub(k+1);
        vector<int> v(k+1);

        int n = nums.size(), m = n/2;
        for (int i = 0; i < m; ++i) {

            int a = max(nums[i], nums[n-1-i]), b = min(nums[i], nums[n-1-i]);

            v[max(k - b, a - 0)]++;

            sub[a - b]++;
        }


        int res = INT_MAX, sum = m;

        for (int x = 0; x <= k; ++x) {
            int T = (sum + 2*(m - sum) - sub[x]);

            //cout << x << " " << sum << " " << sub[x] << " " << T << "\n";

            res = min(res, T);

            sum -= v[x];
        }

        return res;
    }
};
