class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int>mp;
        for(int i = 0; i < n / 2; ++i){
            int j = n - i - 1;
            if(nums[i] > nums[j])swap(nums[i], nums[j]);
            ++mp[abs(nums[i] - nums[j])];
        }
        // int ans = 0;
        // int cha = -1;
        map<int, int>v;
        for(auto [x, y] : mp){
            if(v.count(y)){
                v[y] = min(v[y], x);
            }
            else v[y] = x;
            // if(ans < y){
            //     ans = y;
            //     cha = x;
            // }
            // else if(ans == y){
            //     cha = min(cha, x);
            // }
        }
        vector<int>fuck;
        for(auto [x, y] : v){
            fuck.push_back(y);
        }
        reverse(fuck.begin(), fuck.end());
        int ans = 1e9;
        for(int i = 0; i < min((int)fuck.size(), 100); ++i){
            int sum = 0;
            int x = fuck[i];
            for(int j = 0; j < n / 2; ++j){
                int p = n - j - 1;
                if(abs(nums[j] - nums[p]) == x){
                    continue;
                }
                else {
                    if(nums[j] + x <= k || nums[p] - x >= 0)sum += 1;
                    else sum += 2;
                }
            }
            ans = min(ans, sum);
        }
        return ans;
    }
};
