class Solution {
public:
int sol(vector<int>& n, int&k, int t){

int i=0, j=(n.size()-1);
int ans=0;
            while(i<j){

                    if(abs(n[i]-n[j])==t){
                        i++;
                        j--;
                            continue;
                    }
                    int mn=(min(n[i], n[j]));
                    int mx=(max(n[i], n[j]));
                      if( (mx)>=t or (k-mn)>=t or abs(n[i]-n[j])>t){
                            ans++;
                    }
                    else{
                        ans+=2;
                    }

                i++;
                j--;
            }
return ans;

}
    int minChanges(vector<int>& n, int k) {


            unordered_map<int,int>mp;
            int i=0, j=(n.size()-1);
            while(i<j){


                mp[abs(n[i]-n[j])]++;
                i++;
                j--;
            }
    int ans=(1e5);
        int h=(n.size()/2);
            for(auto& q: mp){

                    if((h-q.second)>=ans){
                        continue;
                    }
                ans=min(ans, sol(n, k, q.first));
              //  cout<<q.first<<" "<<ans<<endl;




            }

        return ans;
    }
};
