class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        diff = [0] * (k + 2)
        for i in range(n // 2):
            a, b = nums[i], nums[n - 1 - i]
            if a > b:
                a, b = b, a
            d = max(b, k - a)
            diff[0] += 1
            diff[d + 1] += 1
            diff[k + 1] -= 2
            c = b - a
            diff[c] -= 1
            diff[c + 1] += 1
        for i in range(1, k + 1):
            diff[i] += diff[i - 1]
        # print(diff)
        return min(diff[: k + 1])
