class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size()/2;
        // vector<int> d(n);
        // for (int i=0; i<n; ++i) d[i] = ;

        std::map<int, int> mp;
        for (int i = 0; i < n; ++i) ++mp[abs(nums[i]-nums[nums.size()-1-i])];

        std::map<int, int, greater<>> len;
        for (int i = 0; i < n; ++i) {
            if (nums[i] <= nums[nums.size()-1-i]) ++len[min(nums[i], k-nums[nums.size()-1-i])];
            else ++len[min(nums[nums.size()-1-i], k-nums[i])];
        }

        int ans = n;
        int acc = 0;
        auto it = len.begin();
        for (auto pr: mp) {
            if (pr.first * 2 <= k) ans = min(ans, n - pr.second);
            else {
                while (it != len.end() && it->first > k - pr.first) {
                    acc += it->second;
                    ++it;
                }
                ans = min(ans, n - pr.second + acc);
            }
        }
        return ans;
    }
};
