class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        mp = defaultdict(int)
        mn = 123123123123
        gaps = []
        for i in range(len(nums) // 2):
            x, y = min(nums[i], nums[len(nums) - i - 1]), max(nums[i], nums[len(nums) - i - 1])
            mp[y - x] += 1
            gaps.append(max(y, k - x))
        gaps.sort()
        for ky, v in mp.items():
            l, r = 0, len(gaps)
            x = 0
            while l < r:
                mid = (l + r) // 2
                if gaps[mid] >= ky:
                    r = mid
                else:
                    l = mid + 1
            mn = min(r * 2 + len(gaps) - r - v, mn)
        return mn
