class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int> cnt(k + 100, 0);
        int n = a.size();
        for(int i = 0; i < n - i - 1; i++){
            if(a[i] > a[n - 1 - i]) swap(a[i], a[n - 1 - i]);
            int d = a[n - 1 - i] - a[i];
            cnt[0] += 1;
            cnt[d] -= 1;
            int e = d + max(a[i], k - a[n - 1 - i]);
            cnt[d + 1] += 1;
            cnt[e + 1] -= 1;
            cnt[e + 1] += 2;
            cnt[k + 1] -= 2;
        }
        int res = n / 2;
        for(int i = 0; i <= k; i++){
            if(i) cnt[i] += cnt[i - 1];
            res = min(res, cnt[i]);
        }
        return res;
    }
};
