class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        cnt = {}
        n = len(nums)
        for i in range(0, n//2):
            x = abs(nums[i]-nums[n-i-1])
            cnt[x] = cnt.get(x, 0) + 1
        # aaa = sorted(cnt.items(), key=lambda k, v: (v, -k))
        # k, v = aaa[-1]
        # ans = 0
        f = [0] * 100001
        for i in range(0, n//2):
            a, b = nums[i], nums[n-i-1]
            top = max([a, k-a, b, k-b])
            f[top] += 1
        for i in range(1, len(f)):
            f[i] += f[i-1]
        ans = n
        for X in range(0, k+1):
            aa = n // 2 - cnt.get(X, 0) + (f[X-1] if X>0 else 0)
            ans = min(ans, aa)
        return ans
