class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> countAbs(k+1);
        for(int i=0;i<n/2;i++){
            countAbs[abs(nums[i]-nums[n-1-i])].push_back(min(nums[i],nums[n-1-i]));
        }
        int maxVal=0;
        for(int i=0;i<=k/2;i++){
            int t=countAbs[i].size();
            maxVal=max(maxVal,t);
        }
        int ans=n/2-maxVal;
        for(int i=k/2+1;i<=k;i++){
            int tmp=n/2-countAbs[i].size();
            if(tmp<ans){
                for(int j=0;j<=2*i-2-k;j++){
                    for(auto x:countAbs[j]){
                        if(x+i>k && x+j-i<0 ) tmp++;
                    }
                    if(tmp>=ans) break;
                }
                ans=min(ans,tmp);
            }
        }
        return ans;
    }
};
