class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        a = [0] * (k+1)
        for i in range(n // 2):
            mm = max(k - min(nums[i], nums[n-i-1]), max(nums[i], nums[n-i-1]))
            if (abs(nums[i] - nums[n-i-1])) == 0:
                if mm == 0:
                    if len(a) > 1:
                        a[1] += 2
                else:
                    a[1] += 1
                    if (mm+1) <= k:
                        a[mm+1] += 1
            else:
                a[0] += 1
                a[abs(nums[i] - nums[n-i-1])] -= 1
                if (abs(nums[i] - nums[n-i-1])+1) <= k:
                    a[abs(nums[i] - nums[n-i-1])+1] += 1
                if (mm+1) <= k:
                    a[mm+1] += 1
        ans = inf
        print(a)
        for i in range(k+1):
            if i > 0:
                a[i] += a[i-1]
            ans = min(ans, a[i])
        return ans
