class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> diff(k+2);
        int ans = INT_MAX;
        for(int i=0;i<nums.size();i++){
            int j = nums.size()-i-1;
            if(j<=i) break;
            int d = abs(nums[j]-nums[i]);
            //0....d....k
            //0 changes [d,d]
            //what is the range in which i can make by using exactly 1 change? 
            //increase the larger number to k
            //[d + k-max(nums[i],nums[j])]
            //[d + min(nums[i], nums[j])] //make the min guy to 0
            int mx =(d + max(min(nums[i], nums[j]), k - max(nums[i], nums[j])));
            //now what is the minimum i can make 
            diff[d]--;
            diff[0]++;
            diff[d+1]++;
            diff[mx+1]++;
        }
        for(int i=1;i<=k+1;i++) diff[i]+=diff[i-1];
        for(int i=0;i<=k;i++) ans=min(ans, diff[i]);
        return ans;
    }
};


